package topic1.vars.input.output;

public class W1_4_Flip {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b, temp;
		a = 5;
		b = 7;
		System.out.println("a=" + a + " ,b=" + b);
		temp = a;
		a = b;
		b = temp;	
		System.out.println("a=" + a + " ,b=" + b);
	}
}
