package topic1.vars.input.output;

public class W1_7_DivMode {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=11, b=2;
		System.out.println(a + " div " + b +" is "+ a / b );
		System.out.println(a + " mod " + b +" is "+ a % b );
		
		a=13;
		b=3;
		System.out.println(a + " div " + b +" is "+ a / b );
		System.out.println(a + " mod " + b +" is "+ a % b );
		
	}
}
