package week01.vars.input.output;

public class TheMathClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
				
		System.out.print("abs(45)="+ Math.abs(-45));
		System.out.print(" sqrt(81)="+Math.sqrt(81));
		System.out.println(" pow(3,2)="+Math.pow(3,2));
		System.out.print(" min(300,4)="+Math.min(300,4));
		System.out.print(" max(3,8)="+Math.max(3, 8));
		System.out.print(" round(7.8)="+Math.round(7.8));
		
		
	}
}
